CREATE DEFINER=`root`@`localhost` PROCEDURE `spBitacora`(
	IN `FechaInicial` DATE,
	IN `FechaFinal` DATE,
	IN `Usuario` VARCHAR(10),
	IN `Proceso` VARCHAR(11)




)
    COMMENT 'Consulta de Bitacora por Usuario y Procesos'
BEGIN

-- DECLARO VARIABLE DE FORMA DE PAGO
   DECLARE sUsuarios  VARCHAR(10);
   
   IF (Usuario = "Todos") THEN
      SET sUsuarios = "";
   ELSE
      SET sUsuarios = Usuario;
   END IF;

   IF Proceso = "Todos" THEN 
		SELECT strUsuarioIde, DATE_FORMAT(fecRegistro,"%d-%m-%Y %h:%i:%s") AS fecRegistro, strProcesoIde	          
	   FROM   bitacora		
		WHERE  Date(fecRegistro) >= FechaInicial
		AND    Date(fecRegistro) <= FechaFinal
	   AND    strUsuarioIde LIKE CONCAT('%',TRIM(sUsuarios),'%');
   ELSE
	   SELECT strUsuarioIde, DATE_FORMAT(fecRegistro,"%d-%m-%Y %h:%i:%s") AS fecRegistro, strProcesoIde          
   	FROM   bitacora	
		WHERE  Date(fecRegistro) >= FechaInicial
		AND    Date(fecRegistro) <= FechaFinal
	   AND    strUsuarioIde LIKE CONCAT('%',TRIM(sUsuarios),'%')
      AND    strProcesoIde = Proceso;
   END IF;
     
   
   

END//
DELIMITER ;

