  	    WHERE  compras.proveedor_id = proveedores.proveedor_id
	    AND    DATE(dateCompraFecha) = CURDATE()
       AND ( dateCompraFecha    LIKE CONCAT('%',TRIM(Filtro),'%') OR
             strCompraFactura   LIKE CONCAT('%',TRIM(Filtro),'%') OR
             strFacturaArchivo  LIKE CONCAT('%',TRIM(Filtro),'%') OR             
             decCompraImporte   LIKE CONCAT('%',TRIM(Filtro),'%') OR
             decCompraDescuento LIKE CONCAT('%',TRIM(Filtro),'%') OR
